        [Fact]
        public void ArithmeticOperators()
        {
            Activity v = Activity.FromBecquerels(1);
            AssertEx.EqualTolerance(-1, -v.Becquerels, BecquerelsTolerance);
            AssertEx.EqualTolerance(2, (Activity.FromBecquerels(3)-v).Becquerels, BecquerelsTolerance);
            AssertEx.EqualTolerance(2, (v + v).Becquerels, BecquerelsTolerance);
            AssertEx.EqualTolerance(10, (v*10).Becquerels, BecquerelsTolerance);
            AssertEx.EqualTolerance(10, (10*v).Becquerels, BecquerelsTolerance);
            AssertEx.EqualTolerance(2, (Activity.FromBecquerels(10)/5).Becquerels, BecquerelsTolerance);
            AssertEx.EqualTolerance(2, Activity.FromBecquerels(10)/Activity.FromBecquerels(5), BecquerelsTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            Activity oneBecquerel = Activity.FromBecquerels(1);
            Activity twoBecquerels = Activity.FromBecquerels(2);

            Assert.True(oneBecquerel < twoBecquerels);
            Assert.True(oneBecquerel <= twoBecquerels);
            Assert.True(twoBecquerels > oneBecquerel);
            Assert.True(twoBecquerels >= oneBecquerel);

            Assert.False(oneBecquerel > twoBecquerels);
            Assert.False(oneBecquerel >= twoBecquerels);
            Assert.False(twoBecquerels < oneBecquerel);
            Assert.False(twoBecquerels <= oneBecquerel);
        }

