        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 m²/l", new AreaVolumeRation(0.123456, AreaVolumeRationUnit.SquareMeterPerLiter).ToString("s1"));
                Assert.Equal("0.12 m²/l", new AreaVolumeRation(0.123456, AreaVolumeRationUnit.SquareMeterPerLiter).ToString("s2"));
                Assert.Equal("0.123 m²/l", new AreaVolumeRation(0.123456, AreaVolumeRationUnit.SquareMeterPerLiter).ToString("s3"));
                Assert.Equal("0.1235 m²/l", new AreaVolumeRation(0.123456, AreaVolumeRationUnit.SquareMeterPerLiter).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

        [Fact]
