        {
            var culture = CultureInfo.InvariantCulture;
            Assert.Equal("0.1 m²/l", new AreaVolumeRation(0.123456, AreaVolumeRationUnit.SquareMeterPerLiter).ToString("s1", culture));
            Assert.Equal("0.12 m²/l", new AreaVolumeRation(0.123456, AreaVolumeRationUnit.SquareMeterPerLiter).ToString("s2", culture));
            Assert.Equal("0.123 m²/l", new AreaVolumeRation(0.123456, AreaVolumeRationUnit.SquareMeterPerLiter).ToString("s3", culture));
            Assert.Equal("0.1235 m²/l", new AreaVolumeRation(0.123456, AreaVolumeRationUnit.SquareMeterPerLiter).ToString("s4", culture));
        }

