        [Fact]
        public void ArithmeticOperators()
        {
            BaudRate v = BaudRate.FromBauds(1);
            AssertEx.EqualTolerance(-1, -v.Bauds, BaudsTolerance);
            AssertEx.EqualTolerance(2, (BaudRate.FromBauds(3)-v).Bauds, BaudsTolerance);
            AssertEx.EqualTolerance(2, (v + v).Bauds, BaudsTolerance);
            AssertEx.EqualTolerance(10, (v*10).Bauds, BaudsTolerance);
            AssertEx.EqualTolerance(10, (10*v).Bauds, BaudsTolerance);
            AssertEx.EqualTolerance(2, (BaudRate.FromBauds(10)/5).Bauds, BaudsTolerance);
            AssertEx.EqualTolerance(2, BaudRate.FromBauds(10)/BaudRate.FromBauds(5), BaudsTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            BaudRate oneBaud = BaudRate.FromBauds(1);
            BaudRate twoBauds = BaudRate.FromBauds(2);

            Assert.True(oneBaud < twoBauds);
            Assert.True(oneBaud <= twoBauds);
            Assert.True(twoBauds > oneBaud);
            Assert.True(twoBauds >= oneBaud);

            Assert.False(oneBaud > twoBauds);
            Assert.False(oneBaud >= twoBauds);
            Assert.False(twoBauds < oneBaud);
            Assert.False(twoBauds <= oneBaud);
        }

