        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 kat/m³", new CatalyticVolumeActivity(0.123456, CatalyticVolumeActivityUnit.KatalPerCubicMeter).ToString("s1"));
                Assert.Equal("0.12 kat/m³", new CatalyticVolumeActivity(0.123456, CatalyticVolumeActivityUnit.KatalPerCubicMeter).ToString("s2"));
                Assert.Equal("0.123 kat/m³", new CatalyticVolumeActivity(0.123456, CatalyticVolumeActivityUnit.KatalPerCubicMeter).ToString("s3"));
                Assert.Equal("0.1235 kat/m³", new CatalyticVolumeActivity(0.123456, CatalyticVolumeActivityUnit.KatalPerCubicMeter).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

