        [Fact]
        public void ToString_ReturnsValueAndUnitAbbreviationInCurrentCulture()
        {
            var prevCulture = Thread.CurrentThread.CurrentUICulture;
            Thread.CurrentThread.CurrentUICulture = CultureInfo.GetCultureInfo("en-US");
            try {
                Assert.Equal("1 C/kg", new IonDose(1, IonDoseUnit.CoulombPerKilogramm).ToString());
            }
            finally
            {
                Thread.CurrentThread.CurrentUICulture = prevCulture;
            }
        }

        [Fact]
        public void ToString_WithSwedishCulture_ReturnsUnitAbbreviationForEnglishCultureSinceThereAreNoMappings()
        {
            // Chose this culture, because we don't currently have any abbreviations mapped for that culture and we expect the en-US to be used as fallback.
            var swedishCulture = CultureInfo.GetCultureInfo("sv-SE");

            Assert.Equal("1 C/kg", new IonDose(1, IonDoseUnit.CoulombPerKilogramm).ToString(swedishCulture));
        }

        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 C/kg", new IonDose(0.123456, IonDoseUnit.CoulombPerKilogramm).ToString("s1"));
                Assert.Equal("0.12 C/kg", new IonDose(0.123456, IonDoseUnit.CoulombPerKilogramm).ToString("s2"));
                Assert.Equal("0.123 C/kg", new IonDose(0.123456, IonDoseUnit.CoulombPerKilogramm).ToString("s3"));
                Assert.Equal("0.1235 C/kg", new IonDose(0.123456, IonDoseUnit.CoulombPerKilogramm).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

        [Fact]
        public void ToString_SFormatAndCulture_FormatsNumberWithGivenDigitsAfterRadixForGivenCulture()
        {
            var culture = CultureInfo.InvariantCulture;
            Assert.Equal("0.1 C/kg", new IonDose(0.123456, IonDoseUnit.CoulombPerKilogramm).ToString("s1", culture));
            Assert.Equal("0.12 C/kg", new IonDose(0.123456, IonDoseUnit.CoulombPerKilogramm).ToString("s2", culture));
            Assert.Equal("0.123 C/kg", new IonDose(0.123456, IonDoseUnit.CoulombPerKilogramm).ToString("s3", culture));
            Assert.Equal("0.1235 C/kg", new IonDose(0.123456, IonDoseUnit.CoulombPerKilogramm).ToString("s4", culture));
        }

