        [Fact]
        public void ArithmeticOperators()
        {
            LuminousEfficacy v = LuminousEfficacy.FromLumenPerWatts(1);
            AssertEx.EqualTolerance(-1, -v.LumenPerWatts, LumenPerWattsTolerance);
            AssertEx.EqualTolerance(2, (LuminousEfficacy.FromLumenPerWatts(3)-v).LumenPerWatts, LumenPerWattsTolerance);
            AssertEx.EqualTolerance(2, (v + v).LumenPerWatts, LumenPerWattsTolerance);
            AssertEx.EqualTolerance(10, (v*10).LumenPerWatts, LumenPerWattsTolerance);
            AssertEx.EqualTolerance(10, (10*v).LumenPerWatts, LumenPerWattsTolerance);
            AssertEx.EqualTolerance(2, (LuminousEfficacy.FromLumenPerWatts(10)/5).LumenPerWatts, LumenPerWattsTolerance);
            AssertEx.EqualTolerance(2, LuminousEfficacy.FromLumenPerWatts(10)/LuminousEfficacy.FromLumenPerWatts(5), LumenPerWattsTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            LuminousEfficacy oneLumenPerWatt = LuminousEfficacy.FromLumenPerWatts(1);
            LuminousEfficacy twoLumenPerWatts = LuminousEfficacy.FromLumenPerWatts(2);

            Assert.True(oneLumenPerWatt < twoLumenPerWatts);
            Assert.True(oneLumenPerWatt <= twoLumenPerWatts);
            Assert.True(twoLumenPerWatts > oneLumenPerWatt);
            Assert.True(twoLumenPerWatts >= oneLumenPerWatt);

            Assert.False(oneLumenPerWatt > twoLumenPerWatts);
            Assert.False(oneLumenPerWatt >= twoLumenPerWatts);
            Assert.False(twoLumenPerWatts < oneLumenPerWatt);
            Assert.False(twoLumenPerWatts <= oneLumenPerWatt);
        }

