        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 lm/W", new LuminousEfficacy(0.123456, LuminousEfficacyUnit.LumenPerWatt).ToString("s1"));
                Assert.Equal("0.12 lm/W", new LuminousEfficacy(0.123456, LuminousEfficacyUnit.LumenPerWatt).ToString("s2"));
                Assert.Equal("0.123 lm/W", new LuminousEfficacy(0.123456, LuminousEfficacyUnit.LumenPerWatt).ToString("s3"));
                Assert.Equal("0.1235 lm/W", new LuminousEfficacy(0.123456, LuminousEfficacyUnit.LumenPerWatt).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

        [Fact]
        public void ToString_SFormatAndCulture_FormatsNumberWithGivenDigitsAfterRadixForGivenCulture()
        {
            var culture = CultureInfo.InvariantCulture;
            Assert.Equal("0.1 lm/W", new LuminousEfficacy(0.123456, LuminousEfficacyUnit.LumenPerWatt).ToString("s1", culture));
            Assert.Equal("0.12 lm/W", new LuminousEfficacy(0.123456, LuminousEfficacyUnit.LumenPerWatt).ToString("s2", culture));
            Assert.Equal("0.123 lm/W", new LuminousEfficacy(0.123456, LuminousEfficacyUnit.LumenPerWatt).ToString("s3", culture));
            Assert.Equal("0.1235 lm/W", new LuminousEfficacy(0.123456, LuminousEfficacyUnit.LumenPerWatt).ToString("s4", culture));
        }

