            AssertEx.EqualTolerance(-1, -v.WattPerSquareMeterSteradians, WattPerSquareMeterSteradiansTolerance);
            AssertEx.EqualTolerance(2, (Radiance.FromWattPerSquareMeterSteradians(3)-v).WattPerSquareMeterSteradians, WattPerSquareMeterSteradiansTolerance);
            AssertEx.EqualTolerance(2, (v + v).WattPerSquareMeterSteradians, WattPerSquareMeterSteradiansTolerance);
            AssertEx.EqualTolerance(10, (v*10).WattPerSquareMeterSteradians, WattPerSquareMeterSteradiansTolerance);
            AssertEx.EqualTolerance(10, (10*v).WattPerSquareMeterSteradians, WattPerSquareMeterSteradiansTolerance);
            AssertEx.EqualTolerance(2, (Radiance.FromWattPerSquareMeterSteradians(10)/5).WattPerSquareMeterSteradians, WattPerSquareMeterSteradiansTolerance);
            AssertEx.EqualTolerance(2, Radiance.FromWattPerSquareMeterSteradians(10)/Radiance.FromWattPerSquareMeterSteradians(5), WattPerSquareMeterSteradiansTolerance);
        }

