            Assert.True(twoWattPerSquareMeterSteradians >= oneWattPerSquareMeterSteradian);

            Assert.False(oneWattPerSquareMeterSteradian > twoWattPerSquareMeterSteradians);
            Assert.False(oneWattPerSquareMeterSteradian >= twoWattPerSquareMeterSteradians);
            Assert.False(twoWattPerSquareMeterSteradians < oneWattPerSquareMeterSteradian);
            Assert.False(twoWattPerSquareMeterSteradians <= oneWattPerSquareMeterSteradian);
        }

