        {
            RadiantIntensity oneWattPerSteradian = RadiantIntensity.FromWattPerSteradians(1);
            RadiantIntensity twoWattPerSteradians = RadiantIntensity.FromWattPerSteradians(2);

            Assert.True(oneWattPerSteradian < twoWattPerSteradians);
            Assert.True(oneWattPerSteradian <= twoWattPerSteradians);
            Assert.True(twoWattPerSteradians > oneWattPerSteradian);
            Assert.True(twoWattPerSteradians >= oneWattPerSteradian);

            Assert.False(oneWattPerSteradian > twoWattPerSteradians);
            Assert.False(oneWattPerSteradian >= twoWattPerSteradians);
            Assert.False(twoWattPerSteradians < oneWattPerSteradian);
            Assert.False(twoWattPerSteradians <= oneWattPerSteradian);
        }

