        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 W/sr", new RadiantIntensity(0.123456, RadiantIntensityUnit.WattPerSteradian).ToString("s1"));
                Assert.Equal("0.12 W/sr", new RadiantIntensity(0.123456, RadiantIntensityUnit.WattPerSteradian).ToString("s2"));
                Assert.Equal("0.123 W/sr", new RadiantIntensity(0.123456, RadiantIntensityUnit.WattPerSteradian).ToString("s3"));
                Assert.Equal("0.1235 W/sr", new RadiantIntensity(0.123456, RadiantIntensityUnit.WattPerSteradian).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

        [Fact]
        public void ToString_SFormatAndCulture_FormatsNumberWithGivenDigitsAfterRadixForGivenCulture()
        {
            var culture = CultureInfo.InvariantCulture;
            Assert.Equal("0.1 W/sr", new RadiantIntensity(0.123456, RadiantIntensityUnit.WattPerSteradian).ToString("s1", culture));
            Assert.Equal("0.12 W/sr", new RadiantIntensity(0.123456, RadiantIntensityUnit.WattPerSteradian).ToString("s2", culture));
            Assert.Equal("0.123 W/sr", new RadiantIntensity(0.123456, RadiantIntensityUnit.WattPerSteradian).ToString("s3", culture));
            Assert.Equal("0.1235 W/sr", new RadiantIntensity(0.123456, RadiantIntensityUnit.WattPerSteradian).ToString("s4", culture));
        }

