        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 J/(g·°C)", new SpecificHeatEnergy(0.123456, SpecificHeatEnergyUnit.JoulePerGramCelciusDegree).ToString("s1"));
                Assert.Equal("0.12 J/(g·°C)", new SpecificHeatEnergy(0.123456, SpecificHeatEnergyUnit.JoulePerGramCelciusDegree).ToString("s2"));
                Assert.Equal("0.123 J/(g·°C)", new SpecificHeatEnergy(0.123456, SpecificHeatEnergyUnit.JoulePerGramCelciusDegree).ToString("s3"));
                Assert.Equal("0.1235 J/(g·°C)", new SpecificHeatEnergy(0.123456, SpecificHeatEnergyUnit.JoulePerGramCelciusDegree).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

        [Fact]
        public void ToString_SFormatAndCulture_FormatsNumberWithGivenDigitsAfterRadixForGivenCulture()
        {
            var culture = CultureInfo.InvariantCulture;
            Assert.Equal("0.1 J/(g·°C)", new SpecificHeatEnergy(0.123456, SpecificHeatEnergyUnit.JoulePerGramCelciusDegree).ToString("s1", culture));
            Assert.Equal("0.12 J/(g·°C)", new SpecificHeatEnergy(0.123456, SpecificHeatEnergyUnit.JoulePerGramCelciusDegree).ToString("s2", culture));
            Assert.Equal("0.123 J/(g·°C)", new SpecificHeatEnergy(0.123456, SpecificHeatEnergyUnit.JoulePerGramCelciusDegree).ToString("s3", culture));
            Assert.Equal("0.1235 J/(g·°C)", new SpecificHeatEnergy(0.123456, SpecificHeatEnergyUnit.JoulePerGramCelciusDegree).ToString("s4", culture));
        }

