        [Fact]
        public void ArithmeticOperators()
        {
            SpecificLength v = SpecificLength.FromMeterPerGrams(1);
            AssertEx.EqualTolerance(-1, -v.MeterPerGrams, MeterPerGramsTolerance);
            AssertEx.EqualTolerance(2, (SpecificLength.FromMeterPerGrams(3)-v).MeterPerGrams, MeterPerGramsTolerance);
            AssertEx.EqualTolerance(2, (v + v).MeterPerGrams, MeterPerGramsTolerance);
            AssertEx.EqualTolerance(10, (v*10).MeterPerGrams, MeterPerGramsTolerance);
            AssertEx.EqualTolerance(10, (10*v).MeterPerGrams, MeterPerGramsTolerance);
            AssertEx.EqualTolerance(2, (SpecificLength.FromMeterPerGrams(10)/5).MeterPerGrams, MeterPerGramsTolerance);
            AssertEx.EqualTolerance(2, SpecificLength.FromMeterPerGrams(10)/SpecificLength.FromMeterPerGrams(5), MeterPerGramsTolerance);
        }

        [Fact]
        public void ComparisonOperators()
        {
            SpecificLength oneMeterPerGram = SpecificLength.FromMeterPerGrams(1);
            SpecificLength twoMeterPerGrams = SpecificLength.FromMeterPerGrams(2);

            Assert.True(oneMeterPerGram < twoMeterPerGrams);
            Assert.True(oneMeterPerGram <= twoMeterPerGrams);
            Assert.True(twoMeterPerGrams > oneMeterPerGram);
            Assert.True(twoMeterPerGrams >= oneMeterPerGram);

            Assert.False(oneMeterPerGram > twoMeterPerGrams);
            Assert.False(oneMeterPerGram >= twoMeterPerGrams);
            Assert.False(twoMeterPerGrams < oneMeterPerGram);
            Assert.False(twoMeterPerGrams <= oneMeterPerGram);
        }

