            Assert.Equal("0.12 m/g", new SpecificLength(0.123456, SpecificLengthUnit.MeterPerGram).ToString("s2", culture));
            Assert.Equal("0.123 m/g", new SpecificLength(0.123456, SpecificLengthUnit.MeterPerGram).ToString("s3", culture));
            Assert.Equal("0.1235 m/g", new SpecificLength(0.123456, SpecificLengthUnit.MeterPerGram).ToString("s4", culture));
        }

