            Thread.CurrentThread.CurrentUICulture = CultureInfo.GetCultureInfo("en-US");
            try {
                Assert.Equal("1 W/K", new ThermalConductance(1, ThermalConductanceUnit.WattPerKelvin).ToString());
            }
            finally
