        [Fact]
        public void ToString_SFormat_FormatsNumberWithGivenDigitsAfterRadixForCurrentCulture()
        {
            var oldCulture = CultureInfo.CurrentUICulture;
            try
            {
                CultureInfo.CurrentUICulture = CultureInfo.InvariantCulture;
                Assert.Equal("0.1 W/K", new ThermalConductance(0.123456, ThermalConductanceUnit.WattPerKelvin).ToString("s1"));
                Assert.Equal("0.12 W/K", new ThermalConductance(0.123456, ThermalConductanceUnit.WattPerKelvin).ToString("s2"));
                Assert.Equal("0.123 W/K", new ThermalConductance(0.123456, ThermalConductanceUnit.WattPerKelvin).ToString("s3"));
                Assert.Equal("0.1235 W/K", new ThermalConductance(0.123456, ThermalConductanceUnit.WattPerKelvin).ToString("s4"));
            }
            finally
            {
                CultureInfo.CurrentUICulture = oldCulture;
            }
        }

        [Fact]
