            Assert.Equal("0.1 W/K", new ThermalConductance(0.123456, ThermalConductanceUnit.WattPerKelvin).ToString("s1", culture));
            Assert.Equal("0.12 W/K", new ThermalConductance(0.123456, ThermalConductanceUnit.WattPerKelvin).ToString("s2", culture));
            Assert.Equal("0.123 W/K", new ThermalConductance(0.123456, ThermalConductanceUnit.WattPerKelvin).ToString("s3", culture));
            Assert.Equal("0.1235 W/K", new ThermalConductance(0.123456, ThermalConductanceUnit.WattPerKelvin).ToString("s4", culture));
        }

